// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Mock USDT Token
contract MockUSDT is ERC20 {
    constructor() ERC20("Mock USDT", "USDT") {
        _mint(msg.sender, 1000000 * 10**6); // 1M USDT with 6 decimals
    }

    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }
}

// Mock WETH Token
contract MockWETH is ERC20 {
    constructor() ERC20("Mock Wrapped Ether", "WETH") {
        _mint(msg.sender, 1000 * 10**18); // 1000 WETH
    }

    function deposit() public payable {
        _mint(msg.sender, msg.value);
    }

    function withdraw(uint256 amount) public {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        _burn(msg.sender, amount);
        payable(msg.sender).transfer(amount);
    }

    receive() external payable {
        deposit();
    }
}

// Mock Uniswap V3 Router for testing
contract MockUniswapV3Router {
    address public immutable WETH;
    uint256 public constant EXCHANGE_RATE = 3000; // 1 ETH = 3000 USDT

    constructor(address _weth) {
        WETH = _weth;
    }

    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    function exactInputSingle(ExactInputSingleParams calldata params)
        external payable returns (uint256 amountOut) {
        require(msg.value == params.amountIn, "ETH amount mismatch");
        require(params.tokenIn == WETH, "Only WETH input supported");

        // Simple mock exchange: 1 ETH = 3000 USDT
        amountOut = msg.value * EXCHANGE_RATE / 10**12; // Adjust for USDT 6 decimals
        require(amountOut >= params.amountOutMinimum, "Insufficient output amount");

        // Mint USDT to recipient
        MockUSDT(params.tokenOut).mint(params.recipient, amountOut);

        return amountOut;
    }
}

// Mock Uniswap V3 Quoter
contract MockUniswapV3Quoter {
    uint256 public constant EXCHANGE_RATE = 3000; // 1 ETH = 3000 USDT

    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
