    }

    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external {
        require(IERC20(asset).transferFrom(msg.sender, address(this), amount), "Transfer failed");

        balances[asset][onBehalfOf] += amount;
        totalSupplied[asset] += amount;

        // Mint aTokens 1:1
        MockAToken(aUsdtToken).mint(onBehalfOf, amount);
    }

    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256) {
        require(balances[asset][msg.sender] >= amount, "Insufficient balance");

        balances[asset][msg.sender] -= amount;
        totalSupplied[asset] -= amount;

        // Burn aTokens and add 5% yield for testing
        MockAToken(aUsdtToken).burn(msg.sender, amount);
        uint256 amountWithYield = amount + (amount * 5 / 100); // 5% yield

        require(IERC20(asset).transfer(to, amountWithYield), "Transfer failed");

        return amountWithYield;
    }

    function getUserAccountData(address user)
        external view returns (
